Script.Inputs = {
    group: 'reference',
}

let translate = 0;

const mouse = {
  x: 0,
  y: 0,
  clickX: 0,
  acceleration: 0,
  speed: 0,
  prevX: 0
};

let isClicked = false;



Script.Signals = {
 
    didMount() {
    const mousePosition = (event) => {
      mouse.x = event.pageX;
      mouse.y = event.pageY;

      if (isClicked) {
        wrapperTop.style.transform = `translate(${
          translate + mouse.x - mouse.clickX
        }px)`;
      }
    };

    const freeFalling = () => {
      wrapperTop.classList.add("transition-enable");
      wrapperTop.style.transform = `translate(${translate + mouse.speed / 5}px)`;
      translate = translate + mouse.speed / 5;
    };

        const wrapperTop = Script.Inputs.group.getDOMElement()
        wrapperTop.addEventListener('click', console.log(wrapperTop))

        document.addEventListener("mousemove", mousePosition);
    wrapperTop.addEventListener("mousedown", (e) => {
    wrapperTop.classList.remove("transition-enable");
    e.preventDefault();
    mouse.clickX = e.pageX;
    isClicked = true;
    });

    wrapperTop.addEventListener("mouseup", () => {
    translate = translate + mouse.x - mouse.clickX;
    freeFalling();
    isClicked = false;
    });
    },
    willUnmount() {
